def kernel_2mm(ni: int, nj: int, nk: int, nl: int, alpha: int, beta: int, A: List[List[int]], B: List[List[int]], C: List[List[int]], D: List[List[int]]) -> List[List[int]]:
    return matrix_elemwise_add(matrix_scalar_mul(beta, D), matrix_matrix_mul(matrix_scalar_mul(alpha, matrix_matrix_mul(A, B)), C))